import re

def nettoyer_transcription(transcription: str) -> str:
    # Remplacements spécifiques demandés
    transcription = re.sub(r"\b3 points de suspension\b", "...", transcription, flags=re.IGNORECASE)
    transcription = re.sub(r"\b2 points de suspension\b", ":", transcription, flags=re.IGNORECASE)
    transcription = re.sub(r"\b2 points\b", ":", transcription, flags=re.IGNORECASE)
    transcription = re.sub(r"\.virgule", ",", transcription, flags=re.IGNORECASE)
    transcription = re.sub(r"\.  virgule,", ",", transcription, flags=re.IGNORECASE)  # Nouveau pattern ajouté
    transcription = re.sub(r",\s*virgule\s*,", ",", transcription, flags=re.IGNORECASE)

    # Supprimer les ", ," multiples (2 ou plus) et ne garder qu'une seule virgule
    transcription = re.sub(r"(,\s*){2,}", ", ", transcription)

    # Remplacer "deux points à la ligne" par ":"
    transcription = re.sub(r"\bdeux points à la ligne\b", ":", transcription, flags=re.IGNORECASE)

    # Remplacer "point virgule" par ";"
    transcription = re.sub(r"\bpoint virgule\b", ";", transcription, flags=re.IGNORECASE)

    # Remplacer "point" verbal sauf si suivi d’un chiffre
    def remplacer_point(match):
        mot_avant = match.group(1) or ''
        suite = match.group(2) or ''
        if suite.strip().isdigit():
            return match.group(0)
        return mot_avant + "."

    transcription = re.sub(r"(\b\w+\s+)?\bpoint(\s+\d+)?\s*,?", remplacer_point, transcription, flags=re.IGNORECASE)

    # Autres ponctuations verbales
    transcription = re.sub(r"\bvirgule\s*,?", ",", transcription, flags=re.IGNORECASE)
    transcription = re.sub(r"\bpoint d'interrogation\s*,?", "?", transcription, flags=re.IGNORECASE)
    transcription = re.sub(r"\bdouble point\s*,?", ":", transcription, flags=re.IGNORECASE)

    # Supprimer les majuscules après virgule, point-virgule, deux-points
    transcription = re.sub(r"(?<=[,;:])\s*([A-Z])", lambda m: m.group(1).lower(), transcription)

    # Nettoyer les ". ." ou "..." en trop
    transcription = re.sub(r"\.\s*\.+", ".", transcription)

    # Nettoyer les espaces autour des ponctuations
    transcription = re.sub(r"\s*([.,;:?!])\s*", r"\1 ", transcription)

    # Majuscule après un point si mot minuscule
    transcription = re.sub(r"\.\s+([a-z])", lambda m: '. ' + m.group(1).upper(), transcription)

    # Nettoyer les espaces multiples
    transcription = re.sub(r"\s+", " ", transcription).strip()

    # Majuscule initiale
    if transcription:
        transcription = transcription[0].upper() + transcription[1:]

    return transcription




